import React, { Component } from "react";

class Item extends Component {
  render() {
    const { titre, image, description, url } = this.props; //Ici on met les props, qui sont titre et image et qu'on récup chez le composant parent, qu'on a créer là bas
    return (
      <div>
        <h4>{titre}</h4>
        <h4>{description}</h4>
        <img src={image} alt="image" />
        <url src={url} />
      </div>
    );
  }
}
export default Item;
